})
export class UserProfileComponent implements OnInit {
  userForm: FormGroup;
  user: User | null = null;
  loading = false;
  error = '';
  success = '';

  constructor(
    private fb: FormBuilder,
    private userService: UserService
  ) {
    this.userForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      bio: ['', Validators.maxLength(500)],
      notifications: [false]
    });
  }

  ngOnInit(): void {
    this.loadUserProfile();
  }

  loadUserProfile(): void {
    this.loading = true;
    this.userService.getCurrentUser()
      .pipe(
        catchError(err => {
          this.error = 'Failed to load user profile: ' + err.message;
          return of(null);
        }),
        finalize(() => this.loading = false)
      )
      .subscribe(user => {
        if (user) {
          this.user = user;
          this.userForm.patchValue({
            name: user.name,
            email: user.email,
            bio: user.bio || '',
            notifications: user.preferences?.notifications || false
          });
        }
      });
  }

  saveProfile(): void {
    if (this.userForm.invalid) {
      this.userForm.markAllAsTouched();
      return;
    }

    this.loading = true;
    this.error = '';
    this.success = '';

    const userData = {
      ...this.userForm.value,
      preferences: {
        notifications: this.userForm.value.notifications
      }
    };

    this.userService.updateUserProfile(userData)
      .pipe(
        catchError(err => {
          this.error = 'Failed to update profile: ' + err.message;
          return of(null);
        }),
        finalize(() => this.loading = false)
      )
      .subscribe(result => {
        if (result) {
          this.success = 'Profile updated successfully';
          this.user = { ...this.user, ...userData } as User;
        }
      });
  }

  resetForm(): void {
    if (this.user) {
      this.userForm.reset({
        name: this.user.name,
        email: this.user.email,
        bio: this.user.bio || '',
        notifications: this.user.preferences?.notifications || false
      });
    } else {
      this.userForm.reset();
    }
    this.error = '';
    this.success = '';
  }
}

